<?php

/**
 * Get HTML template output for the acme records table.
 * It requires the list of acme records array to be passed to populate the table.
 *
 * The output format below:
 * <code>
 * <table class="table table-bordered">
 *        <thead>
 *            <tr>
 *                <th class="col-1" scope="col">Select</th>
 * 			  <th class="col-2" scope="col">Domain</th>
 * 			   <th class="col-1" scope="col">Key</th>
 * 			   <th class="col-5" scope="col">Value</th>
 * 			   <th class="col-2" scope="col">Action</th>
 * 		   </tr>
 * 	   </thead>
 * 	   <tbody>
 * 	       <tr key="challenge_key">
 * 	           <td><input key="challenge_key" type="checkbox"></td>
 * 	           <td>domain</td>
 * 	           <td>challenge_key</td>
 * 	           <td><input type="text" class="form-control" key="challenge_key" value="value" placeholder="Enter value" disabled /></td>
 * 	           <td class="gap-2">
 * 	                   <button class="btn btn-success" action="edit" key="challenge_key">Edit</button>
 * 	                   <button class="btn btn-danger" action="delete" key="challenge_key">Delete</button>
 * 	           </td>
 * 	       </tr>
 * 	   </tbody>
 * </table>
 * </code>
 *
 * @param array $acme_records is the list of acme records to be displayed in the table
 *
 * @return string HTML template output
 */
function get_acme_records_table_html_template( array $acme_records ): string {

	// check if the acme records are empty
	if ( empty( $acme_records ) ) {
		return '<p>No acme records found.</p>';
	}

	// create the HTML template
	$html_template = '<table class="table table-bordered">
            <thead>
                <tr>
                    <th class="col-1" scope="col">Select</th>
                    <th class="col-2" scope="col">Domain</th>
                    <th class="col-1" scope="col">Key</th>
                    <th class="col-5" scope="col">Value</th>
                    <th class="col-2" scope="col">Action</th>
                </tr>
            </thead><tbody>';


	foreach ( $acme_records as $record ) {
		// get the domain name
		$domain = $record->domain;
		// get the challenge key
		$challenge_key = $record->challenge_key;
		// get the value
		$value = $record->value;


		// create the HTML template
		$html_template .= '<tr key="' . esc_html( $challenge_key ) . '">';
		// add the checkbox for select
		$html_template .= '<td><input key="' . esc_html( $challenge_key ) . '" type="checkbox"></td>';
		// add the domain, key and value
		$html_template .= '<td>' . esc_html( $domain ) . '</td>';
		$html_template .= '<td>' . esc_html( $challenge_key ) . '</td>';
		$html_template .= '<td><input type="text" class="form-control"' . ' key="' . esc_html( $challenge_key ) . '" value="' . esc_html( $value ) . '" placeholder="Enter value" disabled /></td>';
		$html_template .= '<td class="gap-2">
							<!-- add select button -->
							<button class="btn btn-success" action="edit" key="' . esc_html( $challenge_key ) . '">Edit</button>
							<button class="btn btn-danger" action="delete" key="' . esc_html( $challenge_key ) . '">Delete</button>
						</td>';
		$html_template .= '</tr>';

	}

	$html_template .= '</tbody>
		</table>';

	return $html_template;
}

