/**
 * Get acme records HTML template output. It fully returns the HTML template.
 * It requires the list of acme records array to be passed to populate the table.
 * It outputs the HTML template with the header and footer.
 *
 * @param array $acme_records is the list of acme records to be displayed in the table
 * @param int $page_index is the page index
 * @param int $total_records is the total number of records
 * @param string|null $query_string is the query string to be used for searching, should be added in the previous and next buttons
 *
 * @return string HTML template output
 */
function get_acme_records_html_template( array $acme_records, int $page_index, int $total_records, ?string $query_string = null): string {

	$html_content = '<link href="https://unpkg.com/material-components-web@latest/dist/material-components-web.min.css" rel="stylesheet">
    <link rel="stylesheet" href="https://fonts.googleapis.com/icon?family=Material+Icons">
    <script src="https://unpkg.com/material-components-web@latest/dist/material-components-web.min.js"></script>
    <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.0.2/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-EVSTQN3/azprG1Anm3QDgpJLIm9Nao0Yz1ztcQTwFspd3yD65VohhpuuCOmLASjC" crossorigin="anonymous"><div class="container">';
	// get the header content
	$html_content .= get_acme_records_page_header_content();
	// get the table content
	$html_content .= get_acme_records_table_html_template( $acme_records );

	// add nonce field for security
	$html_content .= '<input type="hidden" id="ssl_support_for_webbylife_platform_nonce" value="' . esc_html( wp_create_nonce( 'ssl_support_for_webbylife_platform_nonce' ) ) . '">';

	// get the bottom content
	$html_content .= get_acme_records_page_bottom_content( $total_records, $page_index, $query_string );
	$html_content .= '</div>';
	// get the snackbar placeholder
	$html_content .= get_acme_records_snackbar_placeholder();

	// get the js content
	$html_content .= get_acme_records_page_js_content();
	// return the HTML template
	return $html_content;
}

/**
 * Get acme records page header content: IE search, clean all records and delete selected buttons, page title and description
 */
function get_acme_records_page_header_content(): string {

	// create the HTML template
	return '<h1 style="margin-top: 40px;" class="text-center">ACME Challenge Data</h1>
        <p style="margin-bottom: 30px;"  class="lead text-center">List of data added by the WebByLife SSL Support Utility listed here!</p>

        <!-- add search interface and buttons -->
        <div class="row mb-4">
            <div class="col-md-6 flex flex-row">
                <!--inline search and submit button-->
                <form name="search_form" class="d-flex">
                    <input class="form-control me-2" type="text" placeholder="Search" aria-label="Search Entry">
                    <button class="btn btn-outline-success" type="submit">Search</button>
                </form>
            </div>
            <div class="col-md-6 text-end">
                <button onclick="cleanAll()" class="btn btn-dark">Clean All Records</button>
                <button onclick="deleteSelected()" class="btn btn-danger">Delete Selected</button>
            </div>
        </div>';
}

/**
 * Get acme record page js content.
 * It contains the js functions to handle the search, clean all and delete selected buttons, and also edit and save buttons.
 */
function get_acme_records_page_js_content(): string {
	// open the js file from./assets/js/acme_records_data_handler.js
	$js_file = plugin_dir_path( __FILE__ ) . '../assets/js/acme_records_data_handler.js';
	// check if the file exists
	if ( ! file_exists( $js_file ) ) {
		// log that the file does not exist
		error_log( 'JS File required for editing acme entries not found at: ' . $js_file );
		return '';
	}

	// get the file content
	$js_content = file_get_contents( $js_file );

	// check if the file content is empty
	if ( empty( $js_content ) ) {
		// log that the file content is empty
		error_log( 'JS File required for editing acme entries is empty at: ' . $js_file );
		return '';
	}

	// return the js content
	return '<script type="text/javascript">' . $js_content . '</script>';

}

/**
 * Get bottom content for the acme records page, which contains navigation links, such as load previous and next page, and also the total number of records.
 * Load next or previous page button may be disabled if there are no enough records to load, and the button will be disabled.
 * On each page, it will show itsm specified at variables class records. query string needs to be encoded.
 * @param int $total_records is the total number of records
 * @param int $page_index is the page index
 * @param string|null $query_string is the query string to be used for searching, should be added in the previous and next buttons
 * @returns string HTML template output
 */
function get_acme_records_page_bottom_content( int $total_records, int $page_index,  ?string $query_string): string {

	// require once the variables file
	require_once plugin_dir_path( __FILE__ ) . '../utility/Variables.php';
	// calculate the total number of pages
	$total_pages = ceil( $total_records / ACME_RECORDS_ITEMS_PER_PAGE );

	// load prev
	$load_prev_ahref = 'admin.php?page=manage-acme-challenge-data&p=' .($page_index - 1);
	if ( $query_string != null ) {
		$load_prev_ahref .= '&q=' . $query_string;
	}

	// load next
	$load_next_ahref = 'admin.php?page=manage-acme-challenge-data&p=' .($page_index + 1);
	if ( $query_string != null ) {
		$load_next_ahref .= '&q=' . $query_string;
	}


	// if page == 1, do not make any html for the previous button
	$previous_button_content = '';
	if ( $page_index > 1 ) {
		$previous_button_content = '<button class="btn btn-primary"><a href="' . esc_url( $load_prev_ahref ) . '" class="text-white">Load Previous Page</a></button>';
	}

	// if page == total pages, do not make any html for the next button
	$next_button_content = '';
	if ( $page_index < $total_pages ) {
		$next_button_content = '<button class="btn btn-primary"><a href="' . esc_url( $load_next_ahref ) . '" class="text-white">Load Next Page</a></button>';
	}

	// create the HTML template
	// return the HTML template
	return '<div class="row mt-4">
			<div class="col-md-6">
				<p class="text-center"> Showing ' . esc_html( ( $page_index - 1 ) * ACME_RECORDS_ITEMS_PER_PAGE + 1 ) . ' to ' . esc_html( min( $page_index * ACME_RECORDS_ITEMS_PER_PAGE, $total_records ) ) . ' of ' . esc_html( $total_records ) .

	       		' entries</p>
	       					</div>
	       								<div class="col-md-6 text-end">
	       												' . $previous_button_content . '
	       												' . $next_button_content . '
	       								</div>
	       						</div>';


}

